{
    "type": "record",
    "name": "SchemaAnalysisResult",
    "namespace": "com.musicflow.schema",
    "doc": "Schema for storing table analysis results with JSON fields from database",
    "fields": [
        {
            "name": "TABLE_NAME",
            "type": "string",
            "doc": "Name of the table being analyzed"
        },
        {
            "name": "TABLE_NAMESPACE",
            "type": "string",
            "doc": "Namespace or schema of the table"
        },
        {
            "name": "SCHEMA_JSON",
            "type": ["null", "string"],
            "default": null,
            "doc": "JSON string containing the schema definition from database"
        },
        {
            "name": "AI_RESPONSE",
            "type": ["null", "string"],
            "default": null,
            "doc": "JSON string containing AI analysis results from database"
        }
    ]
}
